function BannerInfo({title, desc,link,span,id}){
  return <>
  <h3 className="banner__title">{text}</h3>
          <p className="banner__desc">
            {desc}
            <a href="/">{link}</a>
          </p>
          <span className="banner__sub">{span}</span>
   </>
}


function BannerCont({BannerText}) {
  return (
    <div>
        <section
        id="bannerType"
        className="banner__wrap">
        <h2 className="ir_so">배너 영역</h2>
        <div className="banner__inner">
        {BannerText.map((txt) => (
        <BannerInfo 
          key = {txt.id}
          title = {txt.title}
          desc = {txt.desc}
          link = {txt.link}
          span = {txt.span}
        />
      ))}

        </div>
      </section>
    </div>
  )
}

export default BannerCont
